class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int i = 0;
        int cnt = 0;
        while (i < n) {
            while (i < n) {
                bool is_alter = true;
                int j = 0;
                for (j = 0; j < (k - 1); ++j) {
                    if (colors[(i + j) % n] == colors[(i + j + 1) % n]) {
                        is_alter = false;
                        break;
                    }
                }

                if (!is_alter) {
                    i = i + j + 1;
                } else {
                    ++cnt;
                    break;
                }
            }

            if (i >= n) {
                break;
            }

            while (i < n) {
                ++i;
                if (i == n) {
                    break;
                }
                if (colors[(i + k - 2) % n] != colors[(i + k - 1) % n]) {
                    ++cnt;
                } else {
                    i = i + k - 1;
                    break;
                }
            }
        }
        return cnt;
    }
};
