class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {

        int n = colors.length;

        int [] arr = new int[2 * n];

        for (int i = 0; i < 2 * n; i++) {
            arr[i] = colors[i % n]; 
        }

        int ans = 0;

        int cnt = 0;

        for (int i = 1; i < k; i++) {
            if (colors[i] != colors[i - 1]) cnt++;
        }

        if (cnt == k - 1) ans++;

        for (int i = 1; i < n; i++) {
            if (arr[i] != arr[i - 1]) cnt--;
            if (arr[i + k - 1] != arr[i + k - 2]) cnt++;

            if (cnt == k - 1) ans++;
        }

        return ans;


    }
}
