class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        colors.insert(colors.end(), colors.begin(), colors.end());
        int res = 0;
        for (int i = 0, j = 0; i < n; i++){
            while(j < 2 * n && (j == i || colors[j] != colors[j - 1])){
                j++;
            }
            if (j - i >= k) res++;
        }
        return res;
    }
};
