class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();

        int ans=0;
        int l=0;
        for(int i=1;i<2*n && l<n;i++){
            if(c[(i)%n]==c[(i-1)%n]){
                l=i;
            }
            if(i-l+1>=k){
                ans++;
            }
            while(i-l+1>=k){
                l++;
            }
        }
        return ans;

    }
};
