class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


      int size = colors.size(); 
        if (k == 1) return size;
        int res = 0, alt = 1, i = 1; 
        while (i < size + k - 1) { 
            int current = i, prev = i - 1; 

            if (current >= size) current -= size; 
            if (prev >= size) prev -= size; 

            if (colors[current] != colors[prev]) alt++; 
            else    alt = 1; 

            if (alt >= k) res++; 
            i++; 
        } 
        return res;

    }
};
