class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        string s = "";
        for (auto x : colors) {
            s += (char(x+'0'));
        }
        for (auto x : colors) {
            s += (char(x+'0'));
        }
        auto z_algo = [&] (string s, string t) -> int {
            int ans = 0;
            string text = t+'#'+s;
            int len = text.size();
            vector<int> z(len,0);
            int l = 0, r = 0;
            // cout << text << endl;
            for (int i = 1; i < len; i++) {
                if (i <= r) {
                    z[i] = min(z[i-l],r-i+1);
                }
                int ind = i+z[i];
                if (ind > r) {
                    while (ind < len && text[ind] == text[z[i]]) {
                        z[i]++;
                        ind++;
                    }
                }
                ind = i+z[i]-1;
                if (ind > r) {
                    l = i, r = ind;
                }
                // cout << i << " " << z[i] << endl;
            }
            for (int i = k+1; i < (k+1+n); i++) {
                ans += (z[i] == k);
            }
            return ans;
        };
        string t1 = "", t2 = "";
        for (int i = 0; i < k; i++) {
            if (i%2 == 0) {
                t1 += '1';
                t2 += '0';
            }
            else {
                t1 += '0';
                t2 += '1';
            }
        }
        int ans = z_algo(s,t1) + z_algo(s,t2);
        return ans;
    }
};
