class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        int i=0,j=0,ans=0;
        while(i<n)
        {
            if(j-i+1==k)
            {
                ans++;
                i++;
            }
            int r=j+1;
            if(v[j%n]!=v[r%n])
            j=r;
            else
            {
                i=j+1;
                j=i;
            }
        }
        return ans;
    }
};
