class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        dp = [1 for _ in range(n)]
        for j in range(2):
            for i in range(n):
                if colors[i] != colors[i - 1]:
                    dp[i] = 1 + dp[i - 1]
        sol = 0
        for i in range(n):
            sol += dp[i] >= k
        return sol
