class Solution(object):
    def numberOfAlternatingGroups(self, colors, k):
        """
        :type colors: List[int]
        :type k: int
        :rtype: int
        """
        n = len(colors)
        cs = colors+colors
        m = n+n
        r = 0
        j = 0
        for i in range(1, m):
            if cs[i]==cs[i-1]:
                j=i
            else:
                pass
            if i>=n and i-j+1>=k:
                r+=1
        return r
