class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] f = new int[n];

        int p = 0, q = n - 1;
        while(q != 0 && colors[q] != colors[(q + 1) % n]){
            f[0]++;
            q--;
        }

        for(int i = 1; i < n; i++){
            if(colors[i] == colors[i - 1]){
                f[i] = 0;
            } else {
                f[i] = f[i - 1] == n - 1 ? f[i - 1] : f[i - 1] + 1;
            }
        }

        int res = 0;
        for(int i = 0; i < n; i++){
            if(f[i] >= k - 1){
                res++;
            }
        }
        return res;
    }
}
