class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i = 0; i < k - 1; i++)
            colors.push_back(colors[i]);

        int ans = 0;
        int prev = colors[0];
        for (int i = 0, j = 0; j < colors.size(); j++) {
            if (colors[j] == prev) {
                i = j;
            }

            prev = colors[j];
            if (j - i + 1 > k)
                i++;

            if (j - i + 1 == k) {
                ans++;
            }
        }

        return min(n, ans);
    }
};
