class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int cnt=0;

        if(k==1) return n;

        int len=1;
        int i=1;

        while(i<n+k-1){
            int cur=i;
            int prev=i-1;

            if(cur>=n) cur-=n;
            if(prev>=n) prev-=n;

            if(colors[cur]!=colors[prev]) len++;
            else len=1;

            if(len>=k) cnt++;
            i++;
        }
        return cnt;
    }
};
