class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v = colors;
        for (int i = 0; i < k - 1; ++i) {
            v.push_back(colors[i]);
        }
        int cnt = 0, ans = 0, last = -1;
        for (int i = 0; i < v.size(); ++i) {
            if (last != v[i]) {
                ++cnt;
            } else {
                if (cnt - k + 1 > 0)
                    ans += (cnt - k + 1);
                cnt = 1;
            }
            last = v[i];
        }
        if (cnt - k + 1 > 0)
            ans += (cnt - k + 1);
        return ans;
    }
};
