class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        con = colors[-1] != colors[0]
        pref = [1] * n
        for i in range(n - 2, -1, -1):
            if colors[i] != colors[i + 1]:
                pref[i] += pref[i + 1]
        ans = 0
        for i in range(n):
            cur = pref[i]
            if con and cur >= n - i:
                cur += pref[0]
            ans += cur >= k
        return ans
