class Solution {
    public int numberOfAlternatingGroups(int[] rang, int k) {
        int[] arr = new int[rang.length + k - 1];
        System.arraycopy(rang, 0, arr, 0, rang.length);
        System.arraycopy(rang, 0, arr, rang.length, k - 1);
        int ans = 0, n = 1;
        for (int i = 1; i < arr.length; ++i) {
            if (arr[i] != arr[i - 1]) {
                n++;
            } else {
                n = 1;
            }
            if (n >= k) {
                ans++;
            }
        }
        return ans;
        }
}
