class Solution
{
public:
    int numberOfAlternatingGroups(vector<int> &arr, int p)
    {
        for (int i = 0; i < p - 1; i++)
            arr.push_back(arr[i]);
        int result = 0, count = 1, colors = arr.size();
        for (int i = 1; i < colors; i++)
        {
            if (arr[i] == arr[i - 1])
                count = 1;
            else
            {
                count++;
                if (count >= p)
                    result++;
            }
        }
        return result;
    }
};
