class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& num, int k) {
        vector<int> colors = num;
        for(int i=0;i<num.size();i++) colors.push_back(num[i]);
        for(int i=0;i<num.size();i++) colors.push_back(num[i]);
        int cnt = 0;
        int n = colors.size();
        int len = 1;
        vector<int> left(n,1),right(n,1);

        for(int i=1;i<colors.size();i++){
            if(colors[i]!=colors[i-1]){
                left[i] = left[i-1]+1;

            }
            else 
                left[i] = 1;
        }
        for(int i=colors.size()-2;i>=0;i--){
            if(colors[i]!=colors[i+1]){
                right[i] = right[i+1]+1;
                // right[i] = 1;
            }
        }
        int nm = num.size();
        for(int i=nm;i<nm+nm;i++){
            // cout<<i<<" "<<left[i]<<" "<<right[i]<<"\n";
            int l = left[i];
            int r = right[i];
            // int req = k-1;
            if(l>=k) cnt++;
            if(r>=k) cnt++;
            // cout<<l<<" "<<r<<" "<<req<<"\n";


        }
        // cout<<"\n";
        return cnt/2;
    }
};
