class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0;i<n;i++){
            colors.push_back(colors[i]);
        }
        int prev = -1;
        int cnt = 0;
        int j =  -1;
        int ans = 0;
        map<int,int>mp;
        for(int i = 0;i<n+k-1;i++){
            if(prev!=colors[i]){
                if(cnt==0){
                    j = i;
                }
                cnt++;
                prev = colors[i];
            }else{
                j = i;
                cnt=1;
                prev = colors[i];
            }
            if(cnt>=k){
                ans++;
            }
        }
        return ans;
    }
};
