class Solution {
public:
    int ans=0;
    void solve(vector<int>& colors, int k){
        colors.insert(colors.end(),colors.begin(),colors.begin()+k-1);

        int n=1;
        for(auto i=1;i<colors.size();i++){
            bool check1=colors[i]!=colors[i-1];
            if(check1){
                n=n+1;
            }
            else{
                n=1;
            }
            bool cond1=n>=k;
            if(cond1) ans++;
        }
        return  ;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        solve(colors,k);
        return ans;
    }
};
