class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<n;i++)colors.push_back(colors[i]);
        int sz=1,ans=0,op=0;
        for(int i=1;i<colors.size();i++){

            if(colors[i]+colors[i-1]==1)sz++;
            else{
                if(sz>=k)ans+=sz-k+1;
                sz=1;
            }
            if(i==n-1){
                op=ans;
                 if(sz>=k)op+=sz-k+1;
            }

        }
         if(sz>=k)ans+=sz-k+1;
        if(colors[0]==colors[n-1])return op;
        return ans-2*op+op;
    }
};
