class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int start = colors[0];
        int cnt = 0;
        int len = 1;
        for(int i=1;i<(2*n-1);i++){
            if(i>=n && (i%n)>=(k-1)) break;
            if(len<k){
                if(colors[i%n]!=colors[(i-1)%n]) len++;
                else {
                    len=1;
                    start = colors[i%n];
                }
            }
            if(len==k){
                // cout << i << endl;
                cnt++;
                start = abs(1-start);
                len--;
            }
        }
        return cnt;
    }
};
