#define ll long long int
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        ll n=colors.size();
        ll x=0;
        for(ll i=0;i<n;i++){
            colors.push_back(colors[x]);
            x++;
        }
        ll prev=colors[0];
        ll cur=1;
        vector<ll> track;
        for(ll i=1;i<colors.size();i++){
            ll see=colors[i];
            if(see!=prev){
                cur++;
                prev=see;
            }else{
                track.push_back(cur);
                prev=see;
                cur=1;
            }
        }
        track.push_back(cur);
        ll ans=0;
        ll cnt=0;
        ll cnt2=0;


        for(ll i=0;i<track.size();i++){
            for(ll j=0;j<track[i];j++){
                cnt++;
                cnt2++;


                if(cnt+k-1<=track[i]){
                    ans++;
                }
                 if(cnt2==n){
                    break;
                }

            }
            cnt=0;
            if(cnt2==n){
                break;
            }
        }
        return ans;
    }
};
