class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int groupCount = 0;

        int currentLength = 1;
        for (int i = 1; i < n + k - 1; ++i) {
            int currIndex = i % n;
            int prevIndex = (i - 1) % n;

            // Check for alternating colors
            if (colors[currIndex] != colors[prevIndex]) {
                ++currentLength;
            } else {
                currentLength = 1;
            }


            if (currentLength >= k) {
                ++groupCount;
            }
        }

        return groupCount;
    }

};
