class Solution {
public:
    int n;
    int nextIndex(int index){
        if(index+1<n) return index+1;
        return 0;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        bool prev=false;
        int lastIndex=-1;
        n=colors.size();
        int res=0;
        for(int i=0;i<(int)colors.size();i++){
            //cout<<"deb:: "<<i<<endl;
            //cout<<"i: "<<i<<" "<<"res:"<<res<<" "<<lastIndex<<endl;
            if(prev==false){
                int temp = i;
                int remainingComparison = k-1;
                bool oldu = true;
                while(remainingComparison--){
                    int cur = colors[temp];
                    int nextt = colors[nextIndex(temp)];
                    if(cur==nextt){
                        oldu = false;
                        break;
                    }
                    temp = nextIndex(temp);
                }
                if(oldu == false){
                    if(nextIndex(temp)<n && i < nextIndex(temp)){
                        i = nextIndex(temp) - 1;
                        //cout<<"newI:"<<i<<endl;
                    }
                    else{
                        break;
                    }
                }
                else{
                    prev = true;
                    lastIndex = temp;
                    res++;
                }
            }
            else{
                if(colors[lastIndex]==colors[nextIndex(lastIndex)]){
                    prev = false;
                    lastIndex = -1;
                }
                else{
                    lastIndex = nextIndex(lastIndex);
                    res++;
                }
            }
        }
        return res;
    }
};
