class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), cnt = 1, ans = 0;
        vector<int> v(n + k - 1);
        for (int i = 0; i < n; i++) {
            v[i] = colors[i];
        }
        for (int i = 0; i < k - 1; i++) {
            v[n + i] = colors[i];
        }
        for (int i = 1; i < n + k - 1; i++) {
            if (v[i] != v[i - 1]) {
                cnt++;
            } else {
                cnt = 1;
            }
            if (cnt >= k)
                ans++;
        }
        return ans;
    }
};
