class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int ans=0,j=0;
        for(int i=0;i<n+k;i++){
            if((i-j+1)==k){
                ans++;
                j++;
            }
            if(a[i%n]==a[(i+1)%n]){
                j=i+1;
            }
            // cout << i <<" "<<j<<endl;
            if(j>=n){
                break;
            }
        }
        return ans;
    }
};
