class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int r=0;
        int n=a.size();
        for(int i=0;i<n;i++){
            a.push_back(a[i]);
        }
        int j=0;
        for(int i=0;i<n;i++){
            int f=1;
            while(j-i+1!=k){
                if(a[j]!=a[j+1]){
                    j++;
                }
                else{
                    f=0; break;
                }
            }
            if(f){
                if(j-i+1==k){
                    r++;
                }
            }
            else{
                i=j;
            }
            if(i>=j){
                j=i+1;
            }
        }
        return r;
    }
};
