class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& C, int k) {
        int n = C.size();
        for(int i=0;i<k-1;i++){
            C.push_back(C[i]);
        }

        vector<int> V;
        int ans = 0;
        for(int i=0;i<C.size();i++){
            if(V.empty()){
                V.push_back(C[i]);
                continue;
            }
            if(V.back() == C[i]){
                ans += max(0, (int)V.size() - k + 1);
                V = {C[i]};
            } else {
                V.push_back(C[i]);
            }
        }
        if(V.size()){
            ans += max(0, (int)V.size() - k + 1);
        }
        return ans;
    }
};
