class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int c = 0;
        int ac = 0;
        for (int i = 1; i < k; i++) {
            if (colors[i] != colors[i-1]) ac++;
        }
        for (int i = 0; i < n; i++) {
            if (ac == k - 1) c++;
            if (colors[i] != colors[(i+1) % n]) ac--;
            if (colors[(i+k-1) % n] != colors[(i+k) % n]) ac++;
        }

        return c;

    }
};
