class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0; i<k-1; i++){
            colors.push_back(colors[i]);
        }
        int n=colors.size();
        vector<int> v(n);
        for(int i=1; i<n; i++){
            v[i]=colors[i-1]^colors[i];
        }
        v[0]=0;
        for(int i=1; i<n; i++){
            v[i]+=v[i-1];
        }
        int ct=0;
        for(int i=k-1; i<n; i++){
            if(v[i]-v[i-k+1]==k-1)ct++;
        }
        return ct;
    }
};
