class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int i;
        vector<int>brr(n);
        for(i=0;i<n;i++)
        {
            if(i==0)
            brr[i]=1;
            else if(colors[i]!=colors[i-1])
            brr[i]=brr[i-1]+1;
            else
            brr[i]=1;
        }
        for(i=0;i<k;i++)
        {
            if(i==0)
            {
                if(colors[n-1]!=colors[i])
                brr[i]=brr[n-1]+1;
                else
                brr[i]=1;
            }
            else
            {
                if(colors[i]!=colors[i-1])
                brr[i]=brr[i-1]+1;
                else
                brr[i]=1;
            }
        }
        int c=0;
        for(i=0;i<n;i++)
        {
            if(brr[i]>=k)
            c++;
        }
        for(i=0;i<n;i++)
        cout << brr[i] << " ";
        return c;
    }
};
