class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

        long long ans = 0;

        vector<int> temp = c;
        for(int i = 0; i < k; i++) temp.push_back(c[i]);
        long long n = temp.size();

        map<string,int>mp;
        vector<int>pre(n,0);

        for(int  i = 1; i < n; i++) {
            pre[i] = (pre[i - 1] + abs(temp[i] - temp[i  -1]));
        }
        for(int i = k; i < n; i++) {
            if(pre[i] - pre[i - k + 1] == k - 1) {
                ans++;
            }
        }
        return ans;

    }
};
