#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> pref(2 * n + 1);
        for (int i = 0; i < 2 * n; ++i) {
            pref[i + 1] = pref[i] + (colors[i % n] == colors[(i + 1) % n]);
        }
        int ans = 0;
        for (int i = 1; i <= n; ++i) {
            int j = (i + k - 1);
            if (pref[i + k - 1] == pref[i]) ++ans;
        }
        return ans;
    }
};
