class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector <int> v;
        vector <int> p;
        for(int i = 0; i<colors.size(); i++){
            int b = (i - 1 + colors.size())%colors.size();
            int n = (i + 1)%colors.size();
            if(colors[i] != colors[b] and colors[i] != colors[n]) continue;
            else p.push_back(i);
        }

        int n = p.size();
        int j = 0;
        int ans = 0;
        for(int i = 0; i<colors.size(); i++){
            if(p.size() and p[j] == i) j = (j + 1)%n;
            if(p.size()){
                int x = p[j] < i ? p[j] + colors.size() : p[j];
                int good = x - i - 1;
                if(good >= k-2) {
                    ans++;

                }

            }
            else ans++;
        }
        return ans;
    }
};
