class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count = 0;
        int n = colors.size();
        int l = 0, r = 0;
        int prev = -1;
        while (r <n) {
            if (prev == -1) {
                prev = colors[r];
            }
            if (prev == colors[r]) {
                prev = r;
                l = r;
                continue;
            } else if (prev != colors[r])
                prev = colors[r];
            while (r - l + 1 > k) {
                l++;
            }
            if (r - l + 1 == k)
                count++;
            r++;
        }

        vector<int>ok;
        for(int i=n-k+1;i<=n-1;i++){
            ok.push_back(colors[i]);
        }
        for(int i=0;i<k-1;i++){
            ok.push_back(colors[i]);
        }
        r=0,l=0,n=ok.size(),prev=-1;
        while (r <n) {
            if (prev == -1) {
                prev = ok[r];
            }
            if (prev == ok[r]) {
                prev = r;
                l = r;
                continue;
            } else if (prev != ok[r])
                prev = ok[r];
            while (r - l + 1 > k) {
                l++;
            }
            if (r - l + 1 == k)
                count++;
            r++;
        }
        return count;
    }
};
