class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        l=colors.copy()
        l.extend(colors)
        for i in range(0,len(l),2):
            if l[i]==1:
                l[i]=0
            else:
                l[i]=1
        x=0
        y=0
        r=0
        while r<k:
            x+=(l[r]==0)
            y+=(l[r]==1)
            r+=1
        res=0
        for i in range(len(colors)):
            if x==k or y==k:
                res+=1
            x+=(l[i+k]==0)
            y+=(l[i+k]==1)
            x-=(l[i]==0)
            y-=(l[i]==1)
        return res
