class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int sat=0;
        for(int i=0;i<k;i++){
            if(i==0){
                if(colors[i]!=colors[i+1]) sat++;
            }else if(i==k-1){
                if(colors[i]!=colors[i-1]) sat++;
            }else{
                if(colors[i]!=colors[i-1]&&colors[i]!=colors[i+1]) sat++;
            }
        }
        int ans=0;
        if(sat==k) ans=1; 
        int ptr=0;
        int cursiz=0;
        for(int i=k;i<colors.size();i++){
            if(colors[i-k]!=colors[i-k+1]) sat--;
            if(colors[i-k+1]==colors[i-k]){
                if(colors[i-k+1]!=colors[i-k+2]) sat++;
            }
            if(colors[i-1]!=colors[i-2]){
                if(colors[i]==colors[i-1]) sat--;
            }
            if(colors[i]!=colors[i-1]) sat++;
            if(sat==k) ans++;
        }
        return ans;
    }
};
