import java.util.Vector;

class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int count = 0;

        if (k == 1) {
            return n;
        }

        for(int y=0; y<=12; y++) {

        }

        int altL = 1;
        int i = 1;
        while (i < n + k - 1) {
            int c = i;
            int p = i - 1;
            if (c >= n) {
                c -= n;
            }
            if (p >= n) {
                p -= n;
            }

            if (colors[c] != colors[p]) {
                ++altL;
            } else {
                altL = 1;
            }
            if (altL >= k) {
                ++count;
            }

            ++i;
        }

        return count;
    }
}
