class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size(),ans=0;
        vector<int> ind;
        for(int i=1;i<n;i++){
            if(c[i]==c[i-1]){
                ind.push_back(i);
            }
        }
        if(c[n-1]==c[0]){
            ind.push_back(0);
        }
        if(ind.size()==0){
            return n;
        }
        sort(ind.begin(),ind.end());
        int m=ind.size();
        for(int i=0;i<n;i++){
            int some=i+k-1;
            if(some>=n){
                int spec=some-n;
                int val=lower_bound(ind.begin(),ind.end(),0) - ind.begin();

                int val2=lower_bound(ind.begin(),ind.end(),i+1) - ind.begin();
                if((val>=m || ind[val]>spec) && (val2>=m || ind[val2]>some)){
                    ans++;
                }

            }else{
                int val=lower_bound(ind.begin(),ind.end(),i+1) - ind.begin();
                if(val>=m || ind[val]>some){
                    ans++;
                }
            }
        }
        return ans;
    }
};
