class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i = 0, j = 0, n = colors.size(), ans = 0;
        int prev = !colors[0];

        while(j<2*n && i<n) {
            if(prev == colors[j%n]) i = j;
            if(j-i+1 == k) ans++,i++;
            prev = colors[j%n];
            j++;
        }

        return ans;
    }
};
