class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>& c=colors;
        int n = c.size();
        int l=0, r=1, res=0;
        while(l<n){
            while(r-l+1<k && c[(r-1)%n]!=c[r%n]) r++;
            // cout << l << " " << r << endl;
            if(c[(r-1)%n]==c[r%n]){
                // cout << "a "<< l << " " << r << endl;
                l=r;
                r++;
            }else{
                // cout << "a "<< l << " " << r << endl;
                if(r-l+1==k) res++;
                l++;
            }
        }
        return res;
    }
};
