#define ll long long
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        ll n = colors.size();
        ll ans = 0;
        ll cnt = 1;
        for (int i = 1; i < n; i++) {
            if (colors[i] != colors[i - 1]) {
                cnt++;
            } else {
                cnt = 1;
            }
            if (cnt >= k) {
                ans++;
            }
        }
        if (colors[0] != colors[n - 1]) {
            cnt++;
            if (cnt >= k) {
                ans++;
            }
            for (int i = 1; i < (k-1); i++) {
                if (colors[i] != colors[i - 1]) {
                    cnt++;
                } else {
                    break;
                }
                if (cnt >= k) {
                    ans++;
                }
            }
        }

        return ans;
    }
};
