class Solution {
public:
    int dp[200010];
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> c(colors);
        for(auto &u:colors)
            c.push_back(u);
        int ret=0;
        int n = colors.size();

        for(int i=1;i<2*n;i++){
            if(c[i]!=c[i-1])
                dp[i] = dp[i-1];
            else dp[i] = i;            
            if(i-k+1>=0&&i-k+1<n&&i-dp[i]+1>=k) ret++;
        }
        return ret;

    }
};
