class Solution {
   public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int i, n = a.size();
        for (i = 0; i < k; i++) a.push_back(a[i]);
        int c = 0, p = 0;
        for (i = 1; i < n + k; i++) {
            if (a[i] != a[i - 1])
                p++;
            else
                p = 1;
            if (p >= k) c++;
        }
        return c;
    }
};
