class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for(int i = 0; i < k - 1; i++) a.push_back(a[i]);

        vector<int> nw(a.size(), 0);
        for(int i = 1; i < a.size(); i++){
            if(a[i] != a[i - 1]) nw[i] = 1;
        }
        // for(auto x: nw) cout << x << " ";
        // cout << "\n";
        int ans = 0, cnt = 0;
        for(int i = 0; i < nw.size(); i++){
            if(nw[i] == 1){
                cnt++;
            }
            else{
                if(cnt + 1 >= k){
                    ans += (cnt - k + 2);
                }
                cnt = 0;
            }
        }
        if(cnt + 1 >= k){
            ans += (cnt - k + 2);
        }
        return ans;
    }
};
