class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


        vector<int>temp = colors;

        for(auto it : colors){
            temp.push_back(it);
        }

        int n = 2*colors.size();
        int l = 0;
        int r = 1;
        int cnt = 0;
        set<int>se;

        while(r<n){

            // cout<<l<<" "<<r<<" "<<cnt<<endl;

            if(temp[r] == temp[r-1]){
                l = r;
                r = l+1;
                continue;
            }
            if(r-l==k-1){
                se.insert(l%colors.size());
                cnt++;
                l++;
            }


            r++;
        }

        for(auto it : se){
            cout<<it<<" ";
        }
        return se.size();

    }
};
