class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<pair<int,int>>all;
        int n = colors.size();
        all.push_back({0, 1});
        for(int i=1;i<n;i++){
            int last = all.back().first;
            if((i-last)%2 == 1 && colors[i] != colors[last])all.back().second++;
            else if((i-last)%2 == 0 && colors[i] == colors[last])all.back().second++;
            else all.push_back({i, 1});
        }

        int pos = 0;
        int m = all.size();
        int ret = 0;
        for(int i=0;i<n;i++){
            if(all[pos].first + all[pos].second - 1 < i)pos++;

            int curr = all[pos].first + all[pos].second - i;
            if(pos == all.size() - 1){
                if(colors.back() != colors[0])curr += all[0].second;
            }
            if(curr >= k)ret++;
        }
        return ret;
    }
};
