class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& avengers, int heroes) {
        int n = avengers.size();
        if (n < heroes) return 0;

        int count = 1;
        int result_count = 0;

        for (int i = 1; i < n + heroes - 1; i++) {
            int current = i % n;
            int previous = (current - 1 + n) % n;

            if (avengers[current] != avengers[previous]) {
                count++;
            } else {
                count = 1;
            }

            if (count >= heroes) {
                result_count++;
            }
        }

        return result_count;
    }
};
