class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int numFound = 0;
        int i = 0;
        int j = 1;

        while (j < n + k && i < n) {
            int tempJ = j;
            int tempEnd = tempJ % n;
            while (colors[tempEnd] != colors[(tempEnd - 1 + n) % n] && (tempJ < (n + k - 1))) {
                tempJ += 1;
                tempEnd = tempJ % n;
            }

            numFound += Math.max(tempJ - i - k + 1, 0);
            i = tempJ;
            j = i + 1;
        }
        return numFound;
    }
}
