template<typename T>
class BIT{
private:
	vector<T> tree;
public:
	BIT(int n){
		tree.resize(n+1);
	}

	void clear(){
		fill(tree.begin(), tree.end(),0);
	}

	inline int lowbit(int i){
		return i&-i;
	}

	void add(int i){
		while(i<tree.size()){
			tree[i]++;
			i+= lowbit(i);
		}
	}

	T query(int i){
		T res=0;
		while(i>0){
			res+=tree[i];
			i-= lowbit(i);
		}
		return res;
	}

	T query(int l,int r){
		return query(r)- query(l-1);
	}
};

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        auto a=colors;
        a.insert(a.end(),colors.begin(),colors.end());
        int n=a.size();
        BIT<int> t(n);
        for(int i=0;i<n-1;i++){
            if(a[i]==a[i+1]){
                t.add(i+1);
            }
        }
        int ans=0;
        for(int i=0;i<n/2;i++){
            if(t.query(i+1,i+k-1)==0) ans++;
        }
        return ans;
    }
};
